import React, { Component } from 'react'
import { Button, Card, List } from "antd"
import { connect } from 'react-redux'
// import { nanoid } from 'nanoid'
import {reqCategory} from '../../api'
import { LeftCircleOutlined } from '@ant-design/icons'
import './css/detail.less'
// import { BASE_IMG_URL } from '../../utils/constants'


const { Item } = List
@connect(
    state => ({ productList: state.productList })
)
class Detail extends Component {

    state = {
        categoryId: '',
        desc: '',
        detail: '',
        imgs: [],
        name: '',
        price: ''

    }
    componentDidMount() {
        const reduxProdList = this.props.productList
        const { id } = this.props.match.params
        if (reduxProdList.length !== 0) {
            let result = reduxProdList.find((item) => {
                return item._id === id
            })

            if (result) {
                const { categoryId, desc, detail, imgs, name, price } = result
                this.setState({ categoryId, desc, detail, imgs, name, price })
            }
        } else {
            this.getProdById(id)
        }
    }
    getProdById = async(id)=>{
       /*  const result = await Promise.all([reqCategory(pCategoryId),reqCategory(categoryId)])
        // let result = await reqProdById(id)
        // const {status,data} = result
        if(status === 0){
            console.log(data);
            const {categoryId, desc, detail, imgs, name, price} = data
            this.setState({ categoryId, desc, detail, imgs, name, price })
        }
        console.log(result); */
          // 取出两个分类名称ID数值
    const {pCategoryId,categoryId} = this.props.location.state.product
    // 进行一级分类及二级分类的名称判断
    if (pCategoryId === '0') {
      // 说明显示的是一级分类的商品
      const result = await reqCategory(categoryId)
      // 得到分类名称
      const cName1 = result.data.name
      // 更新状态对象
      this.setState({cName1})
    } else {
      /* 
      // 通过多个await方式发送多个请求：效果不影响但存在效率问题
      // 即：后一个请求必须在前一个请求发送后得到响应结果之后才会发送

      // 二级分类下的产品
      const result1 = await reqCategory(pCategoryId)
      const result2 = await reqCategory(categoryId)
      // 得到分类名称
      const cName1 = result1.data.name
      const cName2 = result2.data.name 
      */

      // 解决方法：一次性发送多个请求 只有都成功了才正常处理
      const results = await Promise.all([reqCategory(pCategoryId),reqCategory(categoryId)])
      const cName1 = results[0].data.name
      const cName2 = results[1].data.name 

      // 更新状态
      this.setState({cName1,cName2})

    }
    }


    render() {
        const { categoryId, desc, detail, imgs, name, price } = this.state
        return (
            <div>
                <Card title={
                    <div className='left-top'>
                        <Button onClick={() => { this.props.history.goBack() }} type="link">
                            <LeftCircleOutlined />
                            <span>商品详情</span>
                        </Button>
                    </div>
                }>
                    <List>
                        <Item>
                            <span className="prod-title">商品名称:</span>
                            <span>{name}</span>
                        </Item>
                        <Item>
                            <span className="prod-title">商品描述:</span>
                            <span>{desc}</span>
                        </Item>
                        <Item>
                            <span className="prod-title">商品价格:</span>
                            <span>{price}</span>
                        </Item>
                        <Item>
                            <span className="prod-title">所属分类:</span>
                            <span>{categoryId}</span>
                        </Item>
                        <Item>
                            <span className="prod-title">商品图片:</span>
                            {/* {
                                imgs.map(img => {
                                    return (
                                        <img
                                            key={nanoid()}
                                            className='product-img'
                                            src={BASE_IMG_URL + img} alt="图片1"
                                            style={{ width: 200, height: 200 }}
                                        />
                                    )
                                })
                            } */}

                            <img
                                className='product-img'
                                src="https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=2343860813,4292986121&fm=15&gp=0.jpg" alt="图片1"
                            />
                            <img className='product-img'
                                src="https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=2343860813,4292986121&fm=15&gp=0.jpg" alt="图片"
                            />
                        </Item>
                        <Item>
                            <span className="prod-title">商品详情:</span>
                            <span dangerouslySetInnerHTML={{ __html: detail }}></span>
                        </Item>
                    </List>
                </Card>
            </div>
        )
    }
}
export default Detail
