import React, { Component } from 'react'
import { Card, Button, Table, message, Modal } from 'antd';
import { PlusCircleOutlined } from '@ant-design/icons'
import { reqCategoryList } from '../../api/index'
import { PAGE_SIZE } from '../../config'
import AddForm from '../../components/AddForm'
import {reqAddCategory} from '../../api/index'

export default class Category extends Component {
    state = {
        categoryList: [],//商品分类列表
        visible: false,//控制弹窗隐藏或显示
        operType: 'add',//操作类型
        showStatus: 0, //标识添加/更新的确认框是否显示 
        isLoading:true,
        currentValue
    }
    componentDidMount() {
        //请求商品分类列表
        this.getCategoryList()
    }
    //获取商品分类列表
    getCategoryList = async () => {
        let result = await reqCategoryList()
        this.setState({isLoading:false})
        const { status, data, msg } = result
        /* data.map((item)=>{
            item.key = item._id
            return item
        }) */
        if (status === 0) {
            this.setState({ categoryList: data.reverse() })
        } else {
            message.error(msg, 1)
        }
    }
    demo = (a) => {
        console.log(a);
    }
    // 展示弹窗
    showAdd = () => {
        this.setState({
            operType: 'add',
            visible: true,
        })
    }
    showUpdate = (item) => {
        console.log(item);
        /* this.setState({
            operType: 'update',
            visible: true
        }) */
    }
    // 响应点击取消
    handleCancel = () => {
        this.setState({ visible: false })
        this.form.resetFields()
    }
    toAdd = async(values)=>{
        let result = await reqAddCategory(values)
        const {status,data,msg} = result
        if(status === 0) {
            let categoryList = [...this.state.categoryList]
            categoryList.unshift(data)
            this.setState({categoryList})
            message.success('新增商品分类成功')
            this.setState({ visible: false })
            this.form.resetFields()

        }
        if(status === 1) message.error(msg,1)
    }
    // 显示添加的确认框
    handleOk = () => {
        const { operType } = this.state
        this.form.validateFields()
            .then(values => {
                if (operType === 'add') {
                    this.toAdd(values)
                    console.log("你要新增");
                }
                if (operType === 'update') {
                    console.log("你要修改");
                }
            })
            .catch(errorInfo => {
                message.warning('表单输入有误，请检查', 1)
                return
            })}
        render() {
            const { categoryList, visible, operType,isLoading } = this.state
            const dataSource = categoryList
            const columns = [
                {
                    title: '分类名',
                    dataIndex: 'name',
                    key: 'name',
                },
                {
                    title: '操作',
                    dataIndex: 'name',
                    key: 'haha',
                    render: (item) => { return <Button type="link" onClick={()=>{this.showUpdate(item)}}>修改分类</Button> },
                    width: '25%',
                    align: 'center'
                },
            ];
            return (
                <>
                    <Card title="" extra={<Button onClick={this.showAdd} type="primary" href="#">
                        <PlusCircleOutlined />添加</Button>}>
                        <Table rowKey="_id" bordered={true} dataSource={dataSource} columns={columns} 
                        pagination={{ pageSize: PAGE_SIZE,showQuickJumper:true}}  isLoading={isLoading}/>;
                </Card>
                    <Modal okText="确定" cancelText="取消" title={operType === 'add' ? '新增分类' : '修改分类'} 
                    visible={visible} onOk={this.handleOk} onCancel={this.handleCancel}>
                        <AddForm /* categorys={categorys} parentId={parentId} */
                            setForm={(form) => { this.form = form }} />
                    </Modal>
                </>
            )
        }
    }
