import React, { Component } from 'react'
import { reqPicsList } from '../../api'
import { Image } from 'antd'
import styled from 'styled-components'
import './css/index.less'
import { nanoid } from 'nanoid'
export default class Home extends Component {
    state = {
        picUrl: '',
        id: '',
        hasScrolled: false
    }
    componentDidMount() {
        window.onscroll = this.onScroll;
        this.getPicsList()
    }
    onScroll = () => {
        if (document.documentElement.scrollTop > 100 && !this.state.hasScrolled) {
            this.setState({ hasScrolled: true })
        } else if (document.documentElement.scrollTop < 100 && this.state.hasScrolled) {
            this.setState({ hasScrolled: false })
        }
    }
    scrollToTop = () => {
        document.documentElement.scrollTop = 0
    }

    getPicsList = async () => {
        let result = await reqPicsList()
        // console.log(result);
        this.setState({ picUrl: result })

    }
    render() {
        const { picUrl } = this.state
        const result = [...picUrl]
        return (
            <div className='box'>
                {/* <img
                    style={{ width: '200px', height: '200px', marginLeft: '100px', marginRight: '100px' }}
                    className='product-img'
                    src="https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=2343860813,4292986121&fm=15&gp=0.jpg" alt="图片1"
                />
                <img className='product-img'
                    src="https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=2343860813,4292986121&fm=15&gp=0.jpg" alt="图片"
                /> */}
                {
                    result.map((item) => {
                        return (
                            <div key={nanoid()} className="item">
                                <Image
                                    key={item.id}
                                    className="imgs"
                                    width={250}
                                    height={200}
                                    src={item.download_url}
                                />
                            </div>
                        )
                    })
                }

                {this.state.hasScrolled && (
                    <ScrollToTopIconContainer onClick={this.scrollToTop}>
                        <div>^</div>
                        <Button>回到顶部</Button>
                    </ScrollToTopIconContainer>
                )}
                <ScrollingWrapperContainer>
                    {this.props.children}
                </ScrollingWrapperContainer>
            </div>
        )
    }
}
const ScrollingWrapperContainer = styled.div`
`
const ScrollToTopIconContainer = styled.div`
  position: fixed;
  bottom: 20px;
  left: calc(50% - 50px);
  z-index: 2;
  cursor: pointer;
  opacity: 0.4;
  text-align: center;
  &:hover {
    opacity: 1;
    animation: wiggle 1s ease;
    animation-iteration-count: 1;
  }
  @keyframes wiggle {
    20% { transform: translateY(6px); }
    40% { transform: translateY(-6px); }
    60% { transform: translateY(4px); }
    80% { transform: translateY(-2px); }
    100% { transform: translateY(0); }
  }
`

const Button = styled.div`
  background: black;
  color: white;
  font-family: Teko;
  font-size: 16px;
  line-height: 32px;
  border-radius: 15px;
  width: 100px;
  padding: 4px 2px 4px 2px;
`
