import React, { Component } from 'react'
import ReactECharts from 'echarts-for-react';
export default class Pie extends Component {
    getOption = () => {
        return {
            title: {
                text: '用户访问来源',
                subtext: '摩尔庄园,快乐童年',
                left: 'center'
            },
            tooltip: {
                trigger: 'item'
            },
            legend: {
                orient: 'vertical',
                left: 'left',
            },
            series: [
                {
                    name: '访问来源',
                    type: 'pie',
                    radius: '50%',
                    data: [
                        { value: 1048, name: '搜索引擎' },
                        { value: 735, name: '直接访问' },
                        { value: 580, name: '邮件营销' },
                        { value: 484, name: '联盟广告' },
                        { value: 300, name: '视频广告' }
                    ],
                    emphasis: {
                        itemStyle: {
                            shadowBlur: 10,
                            shadowOffsetX: 0,
                            shadowColor: 'rgba(0, 0, 0, 0.5)'
                        }
                    }
                }
            ]
        }
    }
    render() {
        return (
            <div className="pit">
                <ReactECharts option={this.getOption()} />
            </div>
        )
    }
}
