import React, { Component } from 'react'
import { Card, Button, Table, message, Modal } from 'antd';
import { PlusCircleOutlined } from '@ant-design/icons'
import { reqCategoryList } from '../../api/index'
import { PAGE_SIZE } from '../../config'
import AddForm from '../../components/AddForm'

export default class Category extends Component {
    state = {
        categoryList: [],//商品分类列表
        visible: false,//控制弹窗隐藏或显示
        operType: 'add'//操作类型
    }
    componentDidMount() {
        //请求商品分类列表
        this.getCategoryList()
    }
    //获取商品分类列表
    getCategoryList = async () => {
        let result = await reqCategoryList()
        const { status, data, msg } = result
        /* data.map((item)=>{
            item.key = item._id
            return item
        }) */
        if (status === 0) {
            this.setState({ categoryList: data })
        } else {
            message.error(msg, 1)
        }
    }
    demo = (a) => {
        console.log(a);
    }

    // 展示弹窗
    showAdd = () => {
        this.setState({
            operType: 'add',
            visible: true,
        })
    }
    showUpdate = () => {
        this.setState({
            operType: 'update',
            visible: true
        })
    }
    // 响应点击取消
    handleCancel = () => {
        this.setState({ visible: false })
        this.form.resetFields()
    }
    // 显示添加的确认框
    handleOk = () => {
        const { operType } = this.state
        this.form.validateFields(async (err, value) => {
            if (err) {
                message.warning('表单输入有误，请检查', 1)
                return
            }
            console.log(value);
            if (operType === 'add') console.log('新增');
            if (operType === 'update') console.log('修改');
            this.form.resetFields()//重置表单
            this.setState({ visible: false })//隐藏弹窗
        })
    }


    render() {
        const { categoryList, visible, operType } = this.state
        const dataSource = categoryList
        const columns = [
            {
                title: '分类名',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: '操作',
                dataIndex: 'name',
                key: 'haha',
                render: (a) => { return <Button type="link" onClick={this.showUpdate}>修改分类</Button> },
                width: '25%',
                align: 'center'
            },
        ];
        return (
            <>
                <Card title="" extra={<Button onClick={this.showAdd} type="primary" href="#">
                    <PlusCircleOutlined />添加</Button>}>
                    <Table rowKey="_id" bordered={true} dataSource={dataSource} columns={columns} pagination={{ pageSize: PAGE_SIZE }} />;
                </Card>
                <Modal okText="确定" cancelText="取消" title={operType === 'add' ? '新增分类' : '修改分类'} visible={visible} onOk={this.handleOk} onCancel={this.handleCancel}>
                    <AddForm /* categorys={categorys} parentId={parentId} */
                        setForm={(form) => { this.form = form }} />
                </Modal>
            </>
        )
    }
}
