import React, { Component } from 'react'
import { Card, Button, Select, Input, Table } from 'antd';
import { PlusCircleOutlined, SearchOutlined } from '@ant-design/icons'
import { reqProductList } from '../../api/index'
import { PAGE_SIZE } from '../../config';



const { Option } = Select;
export default class Product extends Component {
    componentDidMount() {
        this.getProductList()
    }

    getProductList = async()=>{
        let result = await reqProductList(1, PAGE_SIZE)
        const {} = result
    }
    render() {
        const dataSource = [
            {
                key: '1',
                name: '华为',
                desc: '国产手机骄傲--华为',
                price: '1999',
                status: '在售'
            },
            {
                key: '2',
                name: 'apple手机',
                desc: '顶尖科技',
                price: '4999',
                status: '在售'
            },
        ];

        const columns = [
            {
                title: '商品名称',
                width: '18%',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: '商品描述',
                dataIndex: 'desc',
                key: 'desc',
            },
            {
                title: '价格',
                dataIndex: 'price',
                width: '9%',
                key: 'price',
                render: price => `￥${price}`,
                align: 'center',
            },
            {
                title: '状态',
                dataIndex: 'status',
                width: '10%',
                key: 'status',
                align: 'center',
                render: (status) => {
                    return (
                        <>
                            <Button type='primary'>下架</Button><br />
                            <span>{status}</span>
                        </>
                    )
                }
            },
            {
                title: '操作',
                width: '10%',
                dataIndex: 'opera',
                key: 'opera',
                align: 'center',
                render: () => {
                    <>
                        <Button type="link">详情</Button> <br />
                        <Button type="link">修改</Button>
                    </>
                }
            },
        ];
        return (
            <>
                <Card
                    title={
                        <>
                            <Select defaultValue="name" style={{ width: 120 }} /* onChange={handleChange} */>
                                <Option value="name">按名称搜索</Option>
                                <Option value="desc">按描述搜索</Option>
                            </Select>
                            <Input placeholder="关键字"
                                style={{ margin: '0px 1px', width: '20%' }}
                                allowClear={true}
                            />
                            <Button type="primary"><SearchOutlined />搜索</Button>

                        </>
                    }
                    extra={<Button type="primary"><PlusCircleOutlined />添加商品</Button>}
                >
                    <p>Card content</p>
                    <Table
                        dataSource={dataSource}
                        columns={columns}
                        bordered
                    />;
                </Card>
            </>
        )
    }
}
