import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect, Route, Switch } from 'react-router-dom'
import { reqCategoryList } from '../../api/index'
import { Layout } from 'antd'
import { createDeleteUserInfoAction } from '../../redux/actions_creators/login_action'
import './css/admin.less'
import Header from './Header'
import Home from '../../components/Home'
import Category from '../Category'
import Product from '../Product'
import User from '../User'
import Role from '../Role'
import Pie from '../Pie'
import Bar from '../Bar'
import Line from '../Line'
const { Footer, Sider, Content } = Layout;

@connect(
  state => ({ userInfo: state.userInfo }),
  {
    deleteUserInfo: createDeleteUserInfoAction
  }
)
class Admin extends Component {

  componentDidMount() {
    console.log(this.props);
  }

  //退出登录的回调
  logout = () => {
    //触发redux删除所保存的用户信息
    this.props.deleteUserInfo()
  }

  //
  demo = async () => {
    let result = await reqCategoryList()
    console.log(result);
  }

  //在render里，若想实现跳转，最好用<Redirect>              
  render() {
    //从redux中获取user和isLogin
    const { user, isLogin } = this.props.userInfo
    if (!isLogin) {

      console.log('没有登录');
      return <Redirect to="/login" />
    } else {
      console.log('登录了');
      return (
        <>
          {/*  <div>我是Admin组件，你的名字是:{user.username}</div>
          <button onClick={this.logout}>退出登录</button>
          <button onClick={this.demo}>测试获取商品分类列表</button> */}
          <Layout className="admin">
            <Sider className="sider">Sider</Sider>
            <Layout>
              <Header className="header">Header</Header>
              <Content className="content">

                <Switch>
                  <Route path="/Admin/Home" component={Home} />
                  <Route path="/Admin/prod_about/Category" component={Category} />
                  <Route path="/Admin/prod_about/Product" component={Product} />
                  <Route path="/Admin/prod_about/User" component={User} />
                  <Route path="/Admin/prod_about/Role" component={Role} />
                  <Route path="/Admin/prod_about/Category" component={Category} />
                  <Route path="/Admin/prod_about/Category" component={Category} />

                </Switch>

              </Content>
              <Footer className="footer">
                推荐使用谷歌浏览器，获取最佳用户体验
              </Footer>
            </Layout>
          </Layout>



        </>
      )
    }
  }
}

//如下代码中的所有key是控制容器组件传递给UI组件的key
//如下代码中的所有value是控制容器组件传递给UI组件的value

export default Admin
