import React, { Component } from 'react'
import ReactECharts from 'echarts-for-react';
export default class Pie extends Component {



    getOption = () => {
        return {
            title: {
                text: '第一个 ECharts 实例'
            },
            tooltip: {},
            legend: {
                data: ['销量']
            },
            xAxis: {
                data: ["衬衫", "羊毛衫", "雪纺衫", "裤子", "高跟鞋", "袜子"]
            },
            yAxis: {},
            series: [{
                name: '销量',
                type: 'bar',
                data: [5, 20, 36, 10, 10, 20]
            }]
        };
    }
    render() {
        return (
            <>
                <ReactECharts option={this.getOption()} />
            </>
        )
    }
}
