import React, { Component } from 'react'
import { Button, Card, List } from "antd"
import { connect } from 'react-redux'
import { nanoid } from 'nanoid'
import { LeftCircleOutlined } from '@ant-design/icons'
import './css/detail.less'
import {BASE_IMG_URL} from '../../utils/constants'


const { Item } = List
@connect(
    state => ({ productList: state.productList })
)
class Detail extends Component {

    state = {
        categoryId: '',
        desc: '',
        detail: '',
        imgs: [],
        name: '',
        price: ''

    }
    componentDidMount() {
        const reduxProdList = this.props.productList
        const { id } = this.props.match.params
        let result = reduxProdList.find((item) => {
            return item._id === id
        })

        if (result) {
            const { categoryId, desc, detail, imgs, name, price } = result
            this.setState({ categoryId, desc, detail, imgs, name, price })
        }

        console.log(this.props.productList);
    }
    render() {
        const { categoryId, desc, detail, imgs, name, price } = this.state
        return (
            <div>
                <Card title={
                    <div className='left-top'>
                        <Button onClick={() => { this.props.history.goBack() }} type="link">
                            <LeftCircleOutlined />
                            <span>商品详情</span>
                        </Button>
                    </div>
                }>
                    <List>
                        <Item>
                            <span className="prod-name">商品名称:</span>
                            <span>{name}</span>
                        </Item>
                        <Item>
                            <span className="prod-name">商品描述:</span>
                            <span>{desc}</span>
                        </Item>
                        <Item>
                            <span className="prod-name">商品价格:</span>
                            <span>{price}</span>
                        </Item>
                        <Item>
                            <span className="prod-name">所属分类:</span>
                            <span>{categoryId}</span>
                        </Item>
                        <Item>
                            <span className="prod-name">商品图片:</span>
                            {
                                imgs.map((item) => {
                                    return <img key={nanoid()} src={BASE_IMG_URL + item} alt='商品图片' />
                                })
                            }
                            {/* {
                                imgs.map(img => {
                                    return (
                                        <img
                                            key={img}
                                            className='product-img'
                                            src={BASE_IMG_URL + img} alt="图片1"
                                            style={{ width: 200, height: 200 }}
                                        />
                                    )
                                })
                            } */}

                            {/* <img
                                className='product-img'
                                src="https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=2343860813,4292986121&fm=15&gp=0.jpg" alt="图片1"
                            />
                            <img className='product-img'
                                src="https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=2343860813,4292986121&fm=15&gp=0.jpg" alt="图片"
                            /> */}
                        </Item>
                        <Item>
                            <span className="prod-name">商品详情:</span>
                            <span>{detail}</span>
                        </Item>
                    </List>
                </Card>
            </div>
        )
    }
}
export default Detail
