        }
    }
    componentDidMount(){
        //请求商品分类列表
        this.getCategoryList()
    }
    demo = (a) => {
        console.log(a);
    }
    render() {
        const dataSource = [
            {
                key: '1',
                categoryName: '智能手机',
            },
            {
                key: '2',
                categoryName: 'IT书籍',
            },
            {
                key: '3',
                categoryName: '笔记本电脑',
            },
            {
                key: '4',
                categoryName: '台式电脑',
            },
            {
                key: '5',
                categoryName: '耳机音箱',
            },

        ];

        const columns = [
            {
                title: '分类名',
                dataIndex: 'categoryName',
                key: 'categoryName',
                align: 'center'
            },
            {
                title: '操作',
                dataIndex: 'categoryName',
                key: 'age',
                render: (a) => { return <Button type="link" onClick={() => { this.demo(a) }}>修改分类</Button> },
                width: '25%',
                align: 'center'
            },
        ];
        return (
            <>
                <Card title="" extra={<Button type="primary" href="#">
                    <PlusCircleOutlined />添加</Button>}>
                    <Table bordered={true} dataSource={dataSource} columns={columns} />;

                </Card>
            </>
        )
    }
}
