import React, { Component } from 'react'
import { Form, Input, Button, message } from 'antd';
import { connect } from 'react-redux'
import { createDemo1Action, createDemo2Action } from '../../redux/actions_creators/test_action'
import './css/login.less'
import { UserOutlined, LockOutlined } from '@ant-design/icons'
import logo from './img/dora_17.png'
const { Item } = Form

class Login extends Component {

    componentDidMount() {
        console.log(this.props);
    }
    render() {
        return (
            <div className="login">
                <header>
                    <img src={logo} alt="logo" />
                    <h1>商品管理系统{this.props.test}</h1>

                </header>
                <section className="login-content">
                    <h1>用户登陆</h1>
                    {/*  表单函数组件*/}
                    <NormalLoginForm className="Form" />
                </section>
            </div>
        )
    }
}

const NormalLoginForm = () => {
    /* const onFinish = values => {//提交表单且数据验证成功后回调事件
        console.log('Received values of form: ', values);
    }; */

    const tailLayout = {
        wrapperCol: { offset: 10, span: 16 },
    };

    return (
        <Form
            onSubmit={this.handleSubmit}
            /* onFinish={onFinish} className="login-form" */
            initialValues={{
                username: '云显靖',//默认值
            }}
        >
            <Item
                name="username"
                rules={[// 声明式验证: 直接使用别人定义好的验证规则进行验证
                    { required: true, whitespace: true, message: '用户名必须输入' },
                    { min: 4, message: '用户名至少4位' },
                    { max: 12, message: '用户名最多12位' },
                    { pattern: /^[a-zA-Z0-9_]+$/, message: '用户名必须是英文、数字或下划线组成' },
                ]}
            >
                <Input
                    prefix={<UserOutlined className="site-form-item-icon" />}
                    placeholder="用户名"
                />
            </Item>
            <Item
                name="password"
                rules={[
                    {
                        required: true,
                        validator: this.pwdValidator,
                        message: 'Please input your Password!',
                    },
                ]}
            >
                <Input
                    prefix={<LockOutlined className="site-form-item-icon" />}
                    type="password"
                    placeholder="密码"
                />
            </Item>
            <Item {...tailLayout}>
                <Button type="primary" htmlType="submit" className="login-form-button">
                    登录
          </Button>
            </Item>
        </Form>
    );
}



export default connect(
    state => ({ test: state.test }),
    {
        demo1: createDemo1Action,
        demo2: createDemo2Action
    }
)(Login)
