import React, { Component } from 'react'
import {  Modal,Button } from 'antd'
import { FullscreenOutlined, FullscreenExitOutlined } from '@ant-design/icons'
import './css/header.less'
import { connect } from 'react-redux'
import {createDeleteUserInfoAction} from '../../../redux/actions_creators/login_action'
import screenfull from 'screenfull'


@connect(
    state => ({ userInfo: state.userInfo }),
    {
        deleteUser: createDeleteUserInfoAction
    }

)
class Header extends Component {
    state = {
        isFull: true
    }

    //切换全屏按钮的回调
    fullScreen = () => {
        screenfull.toggle()
    }

    componentDidMount() {
        //绑定监听
        screenfull.on('change', () => {
            const isFull = !this.state.isFull
            this.setState({ isFull })
        });
    }

    //退出登录的回调
    logout = () => {
        //触发redux删除所保存的用户信息
        this.props.deleteUser()
    }

    render() {
        const { isFull } = this.state
        const { user } = this.props.userInfo
        return (
            <header className='header'>
                <div className="header-top">
                    <Button size="small" onClick={this.fullScreen}>
                        {isFull ? <FullscreenOutlined /> : <FullscreenExitOutlined />}
                    </Button>
                    <span className="username">欢迎您,{user.username}</span>
                    <Button type="link" onClick={this.logout}>退出登录</Button>
                </div>
                <div className="header-bottom">
                    <div className="header-bottom-left">
                        柱状图
                    </div>
                    <div className="header-bottom-right">
                        时间
                        <img src="https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=1178787126,2434974534&fm=26&gp=0.jpg" alt="天气" />
                        晴&nbsp;&nbsp;&nbsp;温度：2-5
                    </div>
                </div>
            </header>
        )
    }
}
export default Header
