import React, { Component } from 'react'
import { Card, Button, Table, message, Modal } from 'antd';
import { PlusCircleOutlined } from '@ant-design/icons'
import { reqCategoryList } from '../../api/index'
import { PAGE_SIZE } from '../../config'
export default class Category extends Component {
    state = {
        categoryList: [],//商品分类列表
        visible:false
    }
    componentDidMount() {
        //请求商品分类列表
        this.getCategoryList()
    }
    //获取商品分类列表
    getCategoryList = async () => {
        let result = await reqCategoryList()
        const { status, data, msg } = result
        /* data.map((item)=>{
            item.key = item._id
            return item
        }) */
        if (status === 0) {
            this.setState({ categoryList: data })
        } else {
            message.error(msg, 1)
        }
    }
    demo = (a) => {
        console.log(a);
    }

    // 展示弹窗
    showModal = () => {
        this.setState({ visible: true })
    }
    // 响应点击取消
    handleCancel = (e) => {
        console.log(e);
        this.setState({ visible: false })
    }
    // 显示添加的确认框
    handleOk = (e) => {
        console.log(e);
        this.setState({ visible: false })
    }


    render() {
        const { categoryList,visible } = this.state
        const dataSource = categoryList
        const columns = [
            {
                title: '分类名',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: '操作',
                dataIndex: 'name',
                key: 'haha',
                render: (a) => { return <Button type="link" onClick={this.showModal}>修改分类</Button> },
                width: '25%',
                align: 'center'
            },
        ];
        return (
            <>
                <Card title="" extra={<Button onClick={this.showModal} type="primary" href="#">
                    <PlusCircleOutlined />添加</Button>}>
                    <Table rowKey="_id" bordered={true} dataSource={dataSource} columns={columns} pagination={{ pageSize: PAGE_SIZE }} />;
                </Card>
                <Modal okText="确定" cancelText="取消" title="添加分类" visible={visible} onOk={this.handleOk} onCancel={this.handleCancel}>
                    <p>Some contents...</p>
                </Modal>
            </>
        )
    }
}
