import React, { Component } from 'react'
import { Card, Button, Select, Input, Table } from 'antd';
import { PlusCircleOutlined, SearchOutlined } from '@ant-design/icons'
import { reqProductList } from '../../api/index'
import { PAGE_SIZE } from '../../config';



const { Option } = Select;
export default class Product extends Component {
    state = {
        productList: [], //商品列表数据
        total: '',//当前在哪一页
        current: 1//一共有几页
    }
    componentDidMount() {
        this.getProductList()
    }

    getProductList = async (a = 1) => {
        let result = await reqProductList(a, PAGE_SIZE)
        const { status, data, msg } = result
        if (status === 0) {
            console.log(data);
            this.setState({
                productList: data.list,
                total: data.total,
                current: data.pageNum
            })
        }
    }

    render() {
        const { productList, total, current } = this.state
        const dataSource = productList

        const columns = [
            {
                title: '商品名称',
                width: '18%',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: '商品描述',
                dataIndex: 'desc',
                key: 'desc',
            },
            {
                title: '价格',
                dataIndex: 'price',
                width: '9%',
                key: 'price',
                render: price => `￥${price}`,
                align: 'center',
            },
            {
                title: '状态',
                dataIndex: 'status',
                width: '10%',
                key: 'status',
                align: 'center',
                render: (status) => {
                    return (
                        <>
                            <Button type={status === 1 ? 'danger':'primary'}>{status === 1 ? '下架' : '上架'}</Button><br />
                            <span>{status === 1 ? '在售' : '已停售'}</span>
                        </>
                    )
                }
            },
            {
                title: '操作',
                width: '10%',
                // dataIndex: 'opera',
                // key: 'opera',
                align: 'center',
                render: () => {
                    return (
                        <div>
                            <Button type="link">详情</Button><br />
                            <Button type="link">修改</Button>
                        </div>
                    )
                }
            },
        ];
        return (
            <>
                <Card
                    title={
                        <>
                            <Select defaultValue="name" style={{ width: 120 }} /* onChange={handleChange} */>
                                <Option value="name">按名称搜索</Option>
                                <Option value="desc">按描述搜索</Option>
                            </Select>
                            <Input placeholder="关键字"
                                style={{ margin: '0px 1px', width: '20%' }}
                                allowClear={true}
                            />
                            <Button type="primary"><SearchOutlined />搜索</Button>

                        </>
                    }
                    extra={<Button type="primary"
                    ><PlusCircleOutlined />添加商品</Button>}
                >
                    <p>Card content</p>
                    <Table
                        dataSource={dataSource}
                        columns={columns}
                        bordered
                        rowKey="_id"
                        pagination={{
                            total: total,
                            pageSize: PAGE_SIZE,
                            current: current,
                            onChange: this.getProductList
                        }}
                    />
                </Card>
            </>
        )
    }
}
