import React,{Component} from 'react'
import {Form,Input,Tree} from 'antd'
// import PropTypes from 'prop-types'
import menuList from '../../config/menu_config'


const Item = Form.Item 

// 创建角色的form组件
export default class AuthForm extends Component {

    // state = {
    //     treeData: [{
    //         title: '平台权限',
    //         key: 'all',
    //         children: []
    //     }],
    //     checkedKeys:[]
    // }

    constructor(props) {
      super(props)
      const {role} = this.props
      // console.log('role',role);
      this.state = {
        treeData: [{
          title: '平台权限',
          key: 'all',
          children: []
      }],
        checkedKeys: role,//选中的菜单
      }
      // console.log('menus',menus);
      // console.log('state-checkedKeys',this.state.checkedKeys);
    }

    getTreeNodes = (menuList) => {
      return menuList.reduce((pre,item)=>{
        pre.push(
          {
            title: item.title,
            key: item.key,
            children: item.children ? this.getTreeNodes(item.children):null
          }
        )
        return pre
      },[])
    }

    // 为父组件获取最新menus的方法
    getMenus = () => this.state.checkedKeys
    

    // 选中某个node 
    onCheck = checkedKeys => {
        // console.log('onCheck', checkedKeys);
        // setCheckedKeys(checkedKeys);
        this.setState({checkedKeys})
    };

    // 根据新传入的role来更新checkedKeys状态
    UNSAFE_componentWillReceiveProps(nextProps){
      const role = nextProps.role
      console.log(role);
      this.setState({
        checkedKeys: role
      })
      // this.state.checkedKeys = menus
    }

    UNSAFE_componentWillMount(){
        //   this.state.treeData[0].children = this.getTreeNodes(menuList)
        let treeDatas = this.state.treeData
        treeDatas[0].children = this.getTreeNodes(menuList)
        this.setState({
        treeData: treeDatas
        })

    } 
    
    render() {
      // const {role} = this.props
      const {treeData,checkedKeys} = this.state
      // console.log('render之前,',checkedKeys);
      // console.log('render',this.state.checkedKeys);
        // console.log('treeData',treeData);
        return (
            <div>
              <Item label='角色名称'>
                <Input /* value={role.name} */ disabled/>
              </Item>
              <Tree
                checkable //允许选中 
                defaultExpandAll={true}//默认展开所有结点
                checkedKeys={{checked:checkedKeys}}
                onCheck={this.onCheck}
                treeData={treeData} 
                />
            </div>
        )
    }
}

